public class Node {

	private String name;

	private int value;

	private int depth;

	public Node() {
		// TODO Auto-generated constructor stub
	}

	public Node(String name, int value, int depth) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.value = value;
		this.depth = depth;
	}

	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @param name
	 *            the name to set
	 */
	public void setName(String name) {
		this.name = name;
	}

	/**
	 * @return the value
	 */
	public int getValue() {
		return value;
	}

	/**
	 * @param value
	 *            the value to set
	 */
	public void setValue(int value) {
		this.value = value;
	}

	/**
	 * @return the depth
	 */
	public int getDepth() {
		return depth;
	}

	/**
	 * @param depth
	 *            the depth to set
	 */
	public void setDepth(int depth) {
		this.depth = depth;
	}

}
